import axios from 'axios';
import { unmountComponentAtNode } from 'react-dom';
import L from 'leaflet';

import AddMousePosition from '../lib/AddMousePosition';

import CONFIG from '../config.json';
import LAYER_STYLE from '../layers/layerStyles';
import { onEachFeature } from '../layers/layerFeatures';
import { mapNewZoomLevel, mapMousedown } from '../ducks/map';
import { layerPreload, layerLoaded, layerError } from '../ducks/layers';
import { basemapPreload, basemapLoaded, basemapError } from '../ducks/basemaps';
import store from '../store';

/**
 * The main class that contains the Leaflet-based map component
 */
export default class ObliquePhotoMap {
    /**
     * Leaflet popups are tied into React, and the react nodes need to be
     * manually added / removed. The below function removes the React node
     * when a popup is closed
     */
    static onPopupClose (e) {
        let container = e.popup.getContent();
        /**
         * setTimeout hack to get around this current issue with React:
         * https://github.com/facebook/react/issues/3298
         */
        setTimeout(function () {
            unmountComponentAtNode(container);
        },10);
    }
    /**
     * Dispatch a map action every time the user clicks the map
     */
    static onMapMousedown () {
        store.dispatch(mapMousedown());
    }
    /**
     * @param {string} map - the string ID of the html element that the map will be added to
     */
    constructor(map) {
        var self = this;
        this.basemapGroup = L.layerGroup();
        this.layerGroup = L.layerGroup();
        this.map = L.map(map, {
            maxBounds: CONFIG.map.maxExtent,
            layers: [self.basemapGroup, self.layerGroup]
        })
            .fitBounds(CONFIG.map.wisconsinExtent, {
                padding: [10, 10]
            });
        // shim to add non-es6 L.Control.MousePosition
        AddMousePosition(L);
        L.control.mousePosition().addTo(self.map);
        L.control.scale().addTo(self.map);
        this.basemapIndex = {};
        this.layerIndex = {};
        this.dispatchZoom = this.dispatchZoom.bind(this);
        this.updateSize = this.updateSize.bind(this);
        this.map.on('zoomend', self.dispatchZoom);
        this.map.on('mousedown', self.constructor.onMapMousedown);
        this.map.on('popupclose', self.constructor.onPopupClose);
        // set some default values
        this.dispatchZoom();
    }
    /**
     * Dispatch a zoom action every time the map is zoomed
     */
    dispatchZoom () {
        let currentZoom = this.map.getZoom();
        store.dispatch(mapNewZoomLevel(currentZoom));
    }
    /**
     * Create a new map layer
     * @param {string} layerId - a unique identifier for a layer
     * @param {Object} layer - key/value pairs describing a layer
     */
    createLayer(layerId, layer) {
        var self = this;
        store.dispatch(layerPreload(layerId));
        switch (layer.type) {
            case "tileLayer":
                if (typeof layer.url === "undefined") {
                    console.error("Layer " + layerId + " must have a tile URL");
                } else {
                    this.layerIndex[layerId] = L.tileLayer(layer.url, {
                        zIndex: 1
                    });
                    this.layerIndex[layerId].once("load", function () {
                        store.dispatch(layerLoaded(layerId));
                    })
                }
                break;
            case "geojson":
                if (typeof layer.dataLocation === "undefined") {
                    console.error("Layer " + layerId + " must have a data location");
                } else {
                    let layerOptions = {
                        pointToLayer: function (feature, latlng) {
                            // eslint-disable-next-line
                            return new L.circleMarker(latlng, {
                                layerId
                            });
                        },
                        layerId: layerId
                    };
                    layerOptions.onEachFeature = onEachFeature(layerId, self.map);
                    layerOptions.style = LAYER_STYLE(layerId);
                    this.layerIndex[layerId] = L.geoJson(null, layerOptions);
                    axios.get(layer.dataLocation)
                        .then(function(response) {
                            self.layerIndex[layerId].addData(response.data);
                            store.dispatch(layerLoaded(layerId));
                        })
                        .catch(function(error) {
                            console.error(error);
                            store.dispatch(layerError(layerId));
                        });
                }
                break;
            default:
                console.error("Unrecognized layer type in config");
                store.dispatch(layerError(layerId));
                break;
        }
    }
    /**
     * Toggle a layer on or off
     * - Create a layer if it doesn't exist
     * - Toggle it if it already exists and is in the map
     *
     * @param {string} layerId - a unique identifier for a layer
     * @param {Object} layer - key/value pairs describing a layer
     */
    toggleLayer(layerId, layer) {
        if (typeof this.layerIndex[layerId] === "undefined") {
            this.createLayer(layerId, layer);
        }
        if (layer.active === true) {
            this.layerGroup.addLayer(this.layerIndex[layerId]);
        } else {
            this.layerGroup.removeLayer(this.layerIndex[layerId]);
        }
    }
    /**
     * Toggle a basemap on or off
     * - Create if it doesn't exist
     * - Toggle it if it already exists
     *
     * @param {string} basemapId - a unique identifier for a layer
     * @param {Object} basemap - key/value pairs describing a layer
     */
    toggleBasemap(basemapId, basemap) {
        var self = this;
        if (typeof basemap.url === "undefined") {
            console.error("Basemap " + basemapId + " must have a tile URL");
        } else {
            if (typeof this.basemapIndex[basemapId] === "undefined") {
                this.basemapIndex[basemapId] = L.tileLayer(basemap.url, {
                    zIndex: 0
                });
            }
            if (basemap.active === true) {
                this.basemapGroup.clearLayers();
                store.dispatch(basemapPreload(basemapId));
                self.basemapIndex[basemapId].once("load", function () {
                    store.dispatch(basemapLoaded(basemapId));
                })
                this.basemapGroup.addLayer(self.basemapIndex[basemapId]);
            }
        }
    }
    /**
     * Zoom to an extent
     * - wrapper for the Leaflet function map.fitBounds with added padding
     *
     * @param {LatLngBounds|Array[]} extent - [[lat, lng],[lat, lng]]
     */
    zoomToExtent(extent) {
        this.map.fitBounds(extent, {
            padding: [10, 10]
        });
    }
    /**
     * Pan and Zoom to a location
     * - wrapper for the Leaflet function map.setView
     *
     * @param {number} zoom - zoom level
     * @param {LatLng} coordinates - [lat, lng]
     */
    panAndZoom(zoom, coordinates) {
        this.map.setView(coordinates, zoom);
    }
    /**
     * Force leaflet to re-calculate the size of the map within its bounding div
     * - wrapper for the Leaflet function invalidateSize
     */
    updateSize () {
        this.map.invalidateSize();
    }
}

/**
 * basemaps.js Ducks
 *
 * Contains the actions and reducer part that controls basemaps in the map.
 * - Controls adding and removing basemaps
 */
import { BASEMAPS_BY_ID } from '../util'

const initialBasemaps = {};

for(let basemapId in BASEMAPS_BY_ID) {
    initialBasemaps[basemapId] = {
        active: BASEMAPS_BY_ID[basemapId].defaultActive,
        state: 'init'
    }
}

export function toggleBasemap(basemapId) {
    return {
        type: "BASEMAPS:TOGGLE_BASEMAP",
        basemapId
    }
}
export function basemapPreload(basemapId) {
    return {
        type: "BASEMAPS:BASEMAP_PRELOAD",
        basemapId
    }
}
export function basemapLoaded(basemapId) {
    return {
        type: "BASEMAPS:BASEMAP_LOADED",
        basemapId
    }
}
export function basemapError(basemapId) {
    return {
        type: "BASEMAPS:BASEMAP_ERROR",
        basemapId
    }
}

export default function basemaps(state = initialBasemaps, action) {
    let newState = {
        ...state
    };
    switch (action.type) {
        case "BASEMAPS:TOGGLE_BASEMAP":
            {
                let basemapIdToToggle = action.basemapId;
                for (let basemapId in newState) {
                    if (basemapId === basemapIdToToggle) {
                        newState[basemapId] = {
                            ...newState[basemapId],
                            active: true
                        }
                    } else {
                        newState[basemapId] = {
                            ...newState[basemapId],
                            active: false
                        }
                    }
                }
                break;
            }
        case "BASEMAPS:BASEMAP_PRELOAD":
            {
                newState[action.basemapId] = {
                    ...newState[action.basemapId],
                    state: 'loading'
                };
                break;
            }
        case "BASEMAPS:BASEMAP_LOADED":
            {
                newState[action.basemapId] = {
                    ...newState[action.basemapId],
                    state: 'loaded'
                };
                break;
            }
        case "BASEMAPS:BASEMAP_ERROR":
            {
                newState[action.basemapId] = {
                    ...newState[action.basemapId],
                    state: 'error'
                };
                break;
            }
        default:
            break;
    }
    return newState;
}

/**
 * layers.js Ducks
 *
 * Contains the actions and reducer part that controls layers in the map.
 * - Controls adding and removing layers
 * - For each unique layer style present in ech layer, it creates a cache of that style
 *     so it can be presented in the legend.
 */
import { LAYERS_BY_ID, LAYER_GROUPS_BY_ID } from '../util'

export function toggleLayer(layerId) {
    return {
        type: "LAYERS:TOGGLE_LAYER",
        layerId
    }
}

export function legendStyleUpdate(layerId, propertyName, style, geometryType) {
    return {
        type: "LAYER:LEGEND_STYLE_UPDATE",
        layerId,
        propertyName,
        style,
        geometryType
    }
}
export function layerPreload(layerId) {
    return {
        type: "LAYERS:LAYER_PRELOAD",
        layerId
    }
}
export function layerLoaded(layerId) {
    return {
        type: "LAYERS:LAYER_LOADED",
        layerId
    }
}
export function layerError(layerId) {
    return {
        type: "LAYERS:LAYER_ERROR",
        layerId
    }
}

let layerGroupsById = {};
let layersById = {};

// set up our layer group state
for (let layerGroupId in LAYER_GROUPS_BY_ID) {
    layerGroupsById[layerGroupId] = {
        layers: LAYER_GROUPS_BY_ID[layerGroupId].layers
    }
}

// set up our layers state
for (let layerId in LAYERS_BY_ID) {
    let legendStyles = LAYERS_BY_ID[layerId].legendStyles || {};
    layersById[layerId] = {
        active: LAYERS_BY_ID[layerId].defaultActive,
        legendStyles
    }
}

let initialLayers = {
    layersById,
    layerGroupsById
};

export default function layers(state = initialLayers, action) {
    let newState = {
        ...state
    };
    switch (action.type) {
        case "LAYERS:TOGGLE_LAYER": {
            newState.layersById = {
                ...state.layersById,
                [action.layerId]: {
                    ...state.layersById[action.layerId],
                    active: !state.layersById[action.layerId].active
                }
            }
            break;
        }
        case "LAYER:LEGEND_STYLE_UPDATE": {
            let legendStyle = {
                style: action.style,
                geometryType: action.geometryType
            };
            newState.layersById = {
                ...state.layersById,
                [action.layerId]: {
                    ...state.layersById[action.layerId],
                    legendStyles: {
                        ...state.layersById[action.layerId].legendStyles,
                        [action.propertyName]: legendStyle
                    }
                }
            }
            break;
        }
        case "LAYERS:LAYER_PRELOAD":
            {
                newState.layersById = {
                    ...state.layersById,
                    [action.layerId]: {
                        ...state.layersById[action.layerId],
                        state: 'loading'
                    }
                }
                break;
            }
        case "LAYERS:LAYER_LOADED":
            {
                newState.layersById = {
                    ...state.layersById,
                    [action.layerId]: {
                        ...state.layersById[action.layerId],
                        state: 'loaded'
                    }
                }
                break;
            }
        case "LAYERS:LAYER_ERROR":
            {
                newState.layersById = {
                    ...state.layersById,
                    [action.layerId]: {
                        ...state.layersById[action.layerId],
                        state: 'error'
                    }
                }
                break;
            }
        default:
            break;
    }
    return newState;
}

/**
 * map.js Ducks
 *
 * Contains the actions and reducer part that controls map functionality.
 */
import CONFIG from '../config.json';

export function zoomToShoreline(lakeName, countyName) {
    return {
        type: "MAP:ZOOM_TO_SHORELINE",
        lakeName,
        countyName
    }
}
export function doneZooming() {
    return {
        type: "MAP:DONE_ZOOMING"
    }
}
export function resetMapView() {
    return {
        type: "MAP:RESET_VIEW"
    }
}
export function mapNewZoomLevel(zoomLevel) {
    return {
        type: "MAP:NEW_ZOOM_LEVEL",
        zoomLevel
    }
}
export function mapMousedown() {
    return {
        type: "MAP:MOUSEDOWN"
    }
}
export function leafletPopupOpened(coordinates) {
    return {
        type: "MAP:LEAFLET_POPUP_OPENED",
        coordinates
    }
}
const initialMapState = {
    state: {
        action: "none"
    },
    zoom: false
};
export default function map(state = initialMapState, action) {
    let newState = Object.assign({}, state);
    switch (action.type) {
        case "MAP:ZOOM_TO_SHORELINE":
            {
                let shorelineExtent = CONFIG.map.county_shorelines[action.lakeName][action.countyName];
                newState.state = {
                    action: "willZoom",
                    extent: shorelineExtent
                };
                break;
            }
        case "MAP:DONE_ZOOMING":
            {
                newState.state = {
                    action: "none"
                }
                break;
            }
        case "MAP:RESET_VIEW":
            {
                let wisconsinExtent = CONFIG.map.wisconsinExtent;
                newState.state = {
                    action: "willZoom",
                    extent: wisconsinExtent
                };
                break;
            }
        case "MAP:NEW_ZOOM_LEVEL":
            {
                newState.zoom = action.zoomLevel;
                break;
            }
        case "MAP:LEAFLET_POPUP_OPENED":
            {
                if(state.zoom < 10) {
                    newState.state = {
                        action: "willPanAndZoom",
                        zoom: 10,
                        coordinates: action.coordinates
                    };
                }
                break;
            }
        default:
            {
                newState = state;
                break;
            }
    }
    return newState;
}

/**
 * mobile.js Ducks
 *
 * Contains the actions and reducer part that controls mobile functionality.
 * Mobile functionality is present when the window is smaller than 992px.
 * These features include:
 * - Mobile Layer List
 * - Keeping track of window size
 * - When a feature is clicked during mobile mode (<992px)
 * - Mobile Feature Modal
 */

export function openMobileLayerList() {
    return {
        type: "MOBILE:LAYER_POPUP:OPEN"
    }
}
export function closeMobileLayerList() {
    return {
        type: "MOBILE:LAYER_POPUP:CLOSE"
    }
}
export function updateWindowDimensions(height, width) {
    return {
        type: "WINDOW:UPDATE_DIMENSIONS",
        height,
        width
    }
}
export function mobileClickFeature(featureProperties, layerId, featureIndex) {
    return {
        type: "MOBILE:LAYER:CLICK_FEATURE",
        featureProperties,
        layerId,
        featureIndex
    }
}
export function closeMobileFeatureModal() {
    return {
        type: "MOBILE:FEATURE_MODAL:CLOSE"
    }
}
let initialState = {
    window: {
        height: false,
        width: false
    },
    layersPopup: {
        visible: false
    },
    featureModal: {
        visible: false,
        featureProperties: false,
        layerId: false,
        featureIndex: false
    }
};
export default function mobile(state = initialState, action) {
    let newState = Object.assign({}, state);
    let layersPopupState = Object.assign({}, newState.layersPopup);
    let windowState = Object.assign({}, newState.window);
    let featureModalState = Object.assign({}, newState.featureModal);
    switch (action.type) {
        case "MOBILE:LAYER_POPUP:OPEN":
            layersPopupState.visible = true;
            newState.layersPopup = layersPopupState;
            break;
        case "MOBILE:LAYER_POPUP:CLOSE":
            layersPopupState.visible = false;
            newState.layersPopup = layersPopupState;
            break;
        case "WINDOW:UPDATE_DIMENSIONS":
            windowState.height = action.height;
            windowState.width = action.width;
            newState.window = windowState;
            break;
        case "MOBILE:LAYER:CLICK_FEATURE":
            featureModalState.visible = true;
            featureModalState.featureProperties = action.featureProperties;
            featureModalState.layerId = action.layerId;
            featureModalState.featureIndex = action.featureIndex;
            newState.featureModal = featureModalState;
            break;
        case "MOBILE:FEATURE_MODAL:CLOSE":
            featureModalState.visible = false;
            featureModalState.featureProperties = false;
            featureModalState.layerId = false;
            featureModalState.featureIndex = false;
            newState.featureModal = featureModalState;
            break;
        default:
            newState = state;
            break;
    }
    return newState;
}

const initialState = {
    expanded: false
}

export function setNavExpand (expanded) {
    return {
        type: "NAV:SET_EXPANDED",
        expanded
    }
}

export default function nav(state = initialState, action) {
    let newState = {
        ...state
    };
    switch (action.type) {
        case "MAP:MOUSEDOWN":
                newState.expanded = false;
            break;
        case "NAV:SET_EXPANDED":
                newState.expanded = action.expanded;
            break;
        case "MAP:ZOOM_TO_COUNTY":
                newState.expanded = false;
            break;
        default:
            break;
    }
    return newState;
}

/**
 * pinnedFeatures.js Ducks
 *
 * Contains the actions and reducer part that controls feature popups that have
 * been pinned to the page
 */
import uuid from 'uuid';

let initialState = {};

export function newPinnedFeature(layerId, featureProperties, position) {
    return {
        type: "PINNED_FEATURES:NEW",
        layerId,
        featureProperties,
        position
    }
}
export function closePinnedFeature(featureId) {
    return {
        type: "PINNED_FEATURES:CLOSE",
        featureId
    }
}

export default function pinnedFeatures(state = initialState, action) {
    let newState = {
        ...state
    };
    switch (action.type) {
        case "PINNED_FEATURES:NEW": {
            let newPinnedFeatureId = uuid.v4();
            newState[newPinnedFeatureId] = {
                layerId: action.layerId,
                featureProperties: action.featureProperties,
                position: action.position
            };
            break;
        }
        case "PINNED_FEATURES:CLOSE":
            delete newState[action.featureId];
            break;
        default:
            break;
    }
    return newState;
}

/**
 * styles.js
 * This contains style functions that are applied to each layer when they are loaded.
 * Additionally, this file caches the styles applied by each layer, so that when
 * the user activates them in the map, we save memory and we can keep track of all possible styles
 * for use in the legend
 */
import store from '../store';
import { legendStyleUpdate } from '../ducks/layers';

let COLORS = {
    RED: "#F44336",
    GREEN: "#8BC34A",
    YELLOW: "#FFEB3B",
    GRAY: "#9E9E9E",
    BLACK: "#000000"
};

COLORS.BEACH = [
    "#E0E0E0",
    '#a6cee3',
    '#1f78b4',
    '#b2df8a',
    '#33a02c',
    '#fb9a99',
    '#e31a1c',
    '#fdbf6f',
    '#ff7f00',
    '#cab2d6',
    '#6a3d9a',
    '#ffff99'
]

COLORS.PHOTOS = [
    "#9575CD",
    "#7E57C2",
    "#673AB7",
    "#512DA8",
    "#311B92"
];

COLORS.STRUCTURES = [
     "#6D4C41",
     "#3E2723"
];

COLORS.PROFILES = {
    bathy: "#00838F",
    bluff: "#4E342E"
};

// Default styles, if no style is specified for a particular layer
const DEFAULT_STYLES = {
    LineString: {
        weight: 4,
        opacity: 0.9,
        lineCap: "round",
        lineJoin: "round",
        color: COLORS.BLACK
    },
    Point: {
        radius: 1,
        color: COLORS.BLACK,
        strokeColor: COLORS.BLACK,
        weight: 0,
        opacity: 0.8,
        fillOpacity: 0
    },
    MultiLineString: {
        weight: 4,
        opacity: 0.9,
        lineCap: "round",
        lineJoin: "round",
        color: COLORS.BLACK
    }
};

// Individual layer styles are added below, as referenced by ID in config.json
const LAYER_STYLES_BY_ID = {
    backshore_1976: function(subStyleName) {
        let style = {
            ...DEFAULT_STYLES.LineString
        };
        switch (subStyleName) {
            case "Moderately Stable":
                style.color = COLORS.GREEN;
                break;
            case "Moderately Unstable":
                style.color = COLORS.YELLOW;
                break;
            case "No Bluff":
                style.color = COLORS.GRAY;
                break;
            case "Unstable / Failing":
                style.color = COLORS.RED;
                break;
            default:
                style.color = COLORS.BLACK;
                break;
        }
        return style;
    },
    backshore_2007: function(subStyleName) {
        let style = {
            ...DEFAULT_STYLES.LineString
        };
        switch (subStyleName) {
            case "Moderately Stable":
                style.color = COLORS.GREEN;
                break;
            case "Moderately Unstable":
                style.color = COLORS.YELLOW;
                break;
            case "No Bluff":
                style.color = COLORS.GRAY;
                break;
            case "Unstable / Failing":
                style.color = COLORS.RED;
                break;
            default:
                style.color = COLORS.BLACK;
                break;
        }
        return style;
    },
    photos_1976: function() {
        return {
            ...DEFAULT_STYLES.Point,
            color: COLORS.PHOTOS[0],
            strokeColor: COLORS.PHOTOS[0]
        };
    },
    photos_2007: function() {
        return {
            ...DEFAULT_STYLES.Point,
            color: COLORS.PHOTOS[1],
            strokeColor: COLORS.PHOTOS[1],
        };
    },
    structure_1976: function() {
        return {
            ...DEFAULT_STYLES.Point,
            color: COLORS.STRUCTURES[0],
            fillColor: COLORS.STRUCTURES[0],
            opacity: 1
        };
    },
    structure_2007: function() {
        return {
            ...DEFAULT_STYLES.Point,
            color: COLORS.STRUCTURES[1],
            fillColor: COLORS.STRUCTURES[1],
            opacity: 1
        };
    },
    beachclass_1976: function(subStyleName) {
        let style = {
            ...DEFAULT_STYLES.LineString
        };
        switch (subStyleName) {
            case "None":
                style.color = COLORS.BEACH[0];
                break;
            case 'Revetment':
                style.color = COLORS.BEACH[1];
                break;
            case 'Poorly Organized Rip-Rap / Rubble':
                style.color = COLORS.BEACH[2];
                break;
            case 'Seawall / Bulkhead':
                style.color = COLORS.BEACH[3];
                break;
            case 'Groin / Jetty / Offshore Breakwater':
                style.color = COLORS.BEACH[4];
                break;
            case 'Small Boat Dock':
                style.color = COLORS.BEACH[5];
                break;
            case 'Public Marina':
                style.color = COLORS.BEACH[6];
                break;
            case 'Commercial / Industrial Dock':
                style.color = COLORS.BEACH[7];
                break;
            case 'Personal Marina':
                style.color = COLORS.BEACH[8];
                break;
            case 'Offshore Breakwater':
                style.color = COLORS.BEACH[9];
                break;
            default:
                style.opacity = 0;
                break;
        }
        return style;
    },
    beachclass_2007: function(subStyleName) {
        let style = {
            ...DEFAULT_STYLES.LineString
        };
        switch (subStyleName) {
            case "None":
                style.color = COLORS.BEACH[0];
                break;
            case 'Revetment':
                style.color = COLORS.BEACH[1];
                break;
            case 'Poorly Organized Rip-Rap / Rubble':
                style.color = COLORS.BEACH[2];
                break;
            case 'Seawall / Bulkhead':
                style.color = COLORS.BEACH[3];
                break;
            case 'Groin / Jetty / Offshore Breakwater':
                style.color = COLORS.BEACH[4];
                break;
            case 'Small Boat Dock':
                style.color = COLORS.BEACH[5];
                break;
            case 'Public Marina':
                style.color = COLORS.BEACH[6];
                break;
            case 'Commercial / Industrial Dock':
                style.color = COLORS.BEACH[7];
                break;
            case 'Personal Marina':
                style.color = COLORS.BEACH[8];
                break;
            case 'Offshore Breakwater':
                style.color = COLORS.BEACH[9];
                break;
            default:
                style.opacity = 0;
                break;
        }
        return style;
    },
    profiles: function (subStyleName) {
        let style = {
            ...DEFAULT_STYLES.LineString,
            color: COLORS.PROFILES.bluff
        };
        switch (subStyleName) {
            case "Bathymetric Profile":
                style.color = COLORS.PROFILES.bathy;
                break;
            default:
                break;
        }
        return style;
    },
    photos_2016: function () {
        return {
            ...DEFAULT_STYLES.Point,
            color: COLORS.PHOTOS[3],
            strokeColor: COLORS.PHOTOS[3]
        };
    },
    photos_2017: function () {
        return {
            ...DEFAULT_STYLES.Point,
            color: COLORS.PHOTOS[4],
            strokeColor: COLORS.PHOTOS[4]
        };
    },
    photos_2012: function () {
        return {
            ...DEFAULT_STYLES.Point,
            color: COLORS.PHOTOS[2],
            strokeColor: COLORS.PHOTOS[2]
        };
    }
}

/**
 * If a layer should have different styles for features based on a particular feature property
 * i.e. For all features in the Profiles layer:
 *  - feature.type = "bluff" - should be colored red
 *  - feature.type = "bathy" - should be colored blue
 * Return the value of the property that determines the differing style
 * Alternatively, if a layer's style should be the same for all features, just return
 * a string to serve as the label in the legend
 */
function getLayerSubStyleName (layerId, feature) {
    let subStyleName = layerId;
    switch (layerId) {
        case "backshore_1976":
            subStyleName = feature.properties["Bluff Condition Classification"];
            break;
        case "backshore_2007":
            subStyleName = feature.properties["Bluff Condition Classification"];
            break;
        case "photos_1976":
            subStyleName = "1976 Photos";
            break;
        case "photos_2007":
            subStyleName = "2007 Photos";
            break;
        case "structure_1976":
            subStyleName = "Structures";
            break;
        case "structure_2007":
            subStyleName = "Structures";
            break;
        case "beachclass_1976":
            subStyleName = feature.properties["Shore Protection Classification"];
            break;
        case "beachclass_2007":
            subStyleName = feature.properties["Shore Protection Classification"];
            break;
        case "profiles":
            if(feature.properties.type === "bathymetry") {
                subStyleName = "Bathymetric Profile";
            } else {
                subStyleName = "Bluff Profile";
            }
            break;
        case "photos_obl_2016":
            subStyleName = "2016 Photos";
            break;
        case "photos_dm_2016":
            subStyleName = "2016 Photos";
            break;
        case "photos_2017":
            subStyleName = "2017 Photos";
            break;
        case "photos_2012":
            subStyleName = "2012 Photos";
            break;
        default:
            break;
    }
    return subStyleName;
}

const CACHE = {};

function getCachedStyle (layerId, feature) {
    if(typeof CACHE[layerId] !== "undefined") {
        let subStyleName = getLayerSubStyleName(layerId, feature);
        if (typeof CACHE[layerId][subStyleName] !== "undefined") {
            return CACHE[layerId][subStyleName];
        }
    }
    return false;
}

/**
 * Called when a particular style / sub-style doesn't have an associated cache
 * A new style object is created, and cached for future use
 */
function createNewStyle (layerId, feature) {
    let style = null;
    // Get either the singular name for the layer's style or the name of the sub-style for this feature
    let subStyleName = getLayerSubStyleName(layerId, feature);
    // If there's a style set for a particular Layer ID, fetch that style
    // Otherwise, get the default style for that geometry type
    if (typeof LAYER_STYLES_BY_ID[layerId] !== "undefined") {
        style = LAYER_STYLES_BY_ID[layerId](subStyleName);
    } else if (typeof DEFAULT_STYLES[feature.geometry.type] !== "undefined") {
        style = DEFAULT_STYLES[feature.geometry.type];
    }
    // assign the classname property of every style
    let layerIdClass = "layer-" + layerId;
    let layerTypeClass = "layer-type-" + feature.geometry.type;
    if(typeof style.className === "undefined") {
        style.className = "";
    }
    style.className += " " + [layerTypeClass, layerIdClass].join(" ");
    // add the style to the legend
    store.dispatch(legendStyleUpdate(layerId, subStyleName, style, feature.geometry.type));
    // add style to cache
    CACHE[layerId] = CACHE[layerId] || {};
    CACHE[layerId][subStyleName] = style;
    return style;
}

export default function LAYER_STYLE (layerId) {
    return function layerStyle (feature) {
        return getCachedStyle(layerId, feature) || createNewStyle(layerId, feature);
    };
}

export default function AddMousePosition (L) {
    L.Control.MousePosition = L.Control.extend({
        options: {
            position: 'bottomleft',
            separator: ' : ',
            emptyString: 'Unavailable',
            lngFirst: false,
            numDigits: 5,
            lngFormatter: undefined,
            latFormatter: undefined,
            prefix: ""
        },

        onAdd: function (map) {
            this._container = L.DomUtil.create('div', 'leaflet-control-mouseposition');
            L.DomEvent.disableClickPropagation(this._container);
            map.on('mousemove', this._onMouseMove, this);
            this._container.innerHTML=this.options.emptyString;
            return this._container;
        },

        onRemove: function (map) {
            map.off('mousemove', this._onMouseMove)
        },

        _onMouseMove: function (e) {
            var lng = this.options.lngFormatter ? this.options.lngFormatter(e.latlng.lng) : L.Util.formatNum(e.latlng.lng, this.options.numDigits);
            var lat = this.options.latFormatter ? this.options.latFormatter(e.latlng.lat) : L.Util.formatNum(e.latlng.lat, this.options.numDigits);
            var value = this.options.lngFirst ? lng + this.options.separator + lat : lat + this.options.separator + lng;
            var prefixAndValue = this.options.prefix + ' ' + value;
            this._container.innerHTML = prefixAndValue;
        }

    });

    L.Map.mergeOptions({
        positionControl: false
    });

    L.Map.addInitHook(function () {
        if (this.options.positionControl) {
            this.positionControl = new L.Control.MousePosition();
            this.addControl(this.positionControl);
        }
    });

    L.control.mousePosition = function (options) {
        return new L.Control.MousePosition(options);
    };
}

import { createSelector } from 'reselect';
import { LAYERS_BY_ID, LAYER_GROUPS_BY_ID, BASEMAPS_BY_ID } from './util';

const getLayersById = state => state.layers.layersById;
const getBasemapsById = state => state.basemaps;
const getLayerGroupsById = state => state.layers.layerGroupsById;
const getMobileFeatureModal = state => state.mobile.featureModal;

export const getBasemapsByIdWithData = createSelector([getBasemapsById], basemaps => {
    let basemapsWithData = {};
    for(let basemapId in basemaps) {
        basemapsWithData[basemapId] = {
            ...BASEMAPS_BY_ID[basemapId],
            ...basemaps[basemapId]
        }
    }
    return basemapsWithData;
});

export const getLayersByIdWithData = createSelector([getLayersById], layers => {
    let layersWithData = {};
    for (let layerId in layers) {
        layersWithData[layerId] = {
            ...LAYERS_BY_ID[layerId],
            ...layers[layerId]
        };
    }
    return layersWithData;
});

export const getLayerGroupsByIdWithData = createSelector([getLayerGroupsById], layerGroups => {
    let layersGroupsWithData = {};
    for (let layerGroupId in layerGroups) {
        layersGroupsWithData[layerGroupId] = {
            ...LAYER_GROUPS_BY_ID[layerGroupId]
        };
    }
    return layersGroupsWithData;
});

export const mapLayerGroupsToLayers = createSelector(
    [getLayerGroupsByIdWithData, getLayersByIdWithData],
    (layerGroups, layers) => {
        let mappedLayerGroups = {};
        for (let layerGroupId in layerGroups) {
            mappedLayerGroups[layerGroupId] = {
                ...layerGroups[layerGroupId],
                layers: {}
            };
            for (let layerId of layerGroups[layerGroupId].layers) {
                mappedLayerGroups[layerGroupId].layers[layerId] = layers[layerId];
            }
        }
        return mappedLayerGroups;
    }
);

export const getActiveBasemapId = createSelector([getBasemapsById], basemaps => {
    let activeBasemapId = "";
    for(let basemapId in basemaps) {
        if(basemaps[basemapId].active === true) {
            activeBasemapId = basemapId;
        }
    }
    return activeBasemapId;
});

export const getActiveLayers = createSelector(getLayersById, layers => {
    let activeLayers = [];
    for (let layerId in layers) {
        if (layers[layerId].active === true) {
            activeLayers.push(layerId);
        }
    }
    return activeLayers;
});

export const getActiveLayerStyleTypes = createSelector(
    [getLayersByIdWithData, getLayerGroupsByIdWithData, getActiveLayers],
    (layers, layerGroups, activeLayers) => {
        let stylesByLayerId = {};
        for (let layerId of activeLayers) {
            let layer = layers[layerId];
            let layerName = layer.name || layerId;
            let layerGroupId = layer.layerGroupId;
            let layerGroupName = layerGroupId;
            if (typeof layerGroups[layerGroupId] !== "undefined") {
                layerGroupName = layerGroups[layerGroupId].name || layerGroupId;
            }
            let legendStyles = layer.legendStyles;
            let styles = [];
            for (let styleName in legendStyles) {
                let styleIconClassNames = ["fa"];
                let iconStyle = {
                    color: "#000000"
                };
                if (
                    legendStyles[styleName].geometryType === "LineString"
                    || legendStyles[styleName].geometryType === "MultiLineString"
                ) {
                    styleIconClassNames.push("fa-minus");
                    iconStyle.color = legendStyles[styleName].style.color;
                } else if (legendStyles[styleName].geometryType === "Point") {
                    styleIconClassNames.push("fa-circle");
                    iconStyle.color = legendStyles[styleName].style.strokeColor;
                }
                if (styleName === "null") {
                    styleName = "(No Value)";
                }
                styles.push({
                    styleName,
                    iconStyle,
                    styleIconClassNames
                });
            }
            styles = styles.sort(function(a, b) {
                if (a.styleName < b.styleName) {
                    return -1;
                }
                if (a.styleName > b.styleName) {
                    return 1;
                }
                return 0;
            });
            stylesByLayerId[layerId] = {
                layerName,
                layerGroupName,
                styles
            };
        }
        return stylesByLayerId;
    }
);

export const getMobileFeaturePopupProps = createSelector(
    [getLayersByIdWithData, getLayerGroupsByIdWithData, getMobileFeatureModal],
    (layers, layerGroups, featureModal) => {
        if (typeof featureModal.layerId !== "undefined" && featureModal.layerId !== false) {
            let layerId = featureModal.layerId;
            let layerName = layers[layerId].name;
            let layerGroupId = layers[layerId].layerGroupId;
            let layerGroupName = layerGroups[layerGroupId].name;
            return {
                ...featureModal,
                layerName,
                layerGroupName
            }
        }
        return {
            ...featureModal,
            layerName: "",
            layerGroupName: ""
        };
    }
);

import { createStore, combineReducers } from 'redux';
import layers from './ducks/layers';
import basemaps from './ducks/basemaps';
import map from './ducks/map';
import mobile from './ducks/mobile';
import pinnedFeatures from './ducks/pinnedFeatures';
import nav from './ducks/nav';

const theReducer = combineReducers({
    layers,
    basemaps,
    map,
    mobile,
    pinnedFeatures,
    nav
});
const store = createStore(
    theReducer,
    // eslint-disable-next-line no-underscore-dangle
    window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
);
export default store;

import CONFIG from './config.json';

const LAYERS_BY_ID = {};
const LAYER_GROUPS_BY_ID = {}
const BASEMAPS_BY_ID = {};

for (let basemapId in CONFIG.map.basemaps) {
    BASEMAPS_BY_ID[basemapId] = {
        ...CONFIG.map.basemaps[basemapId],
        state: 'init'
    }
    BASEMAPS_BY_ID[basemapId].name = BASEMAPS_BY_ID[basemapId].name || basemapId;
}

// Set up layer groups, default values
for (let layerGroupId in CONFIG.map.layers) {
    let layerGroupProperties = CONFIG.map.layers[layerGroupId];
    // reassign some values
    let name = layerGroupProperties.name || layerGroupId;
    let layers = [];
    LAYER_GROUPS_BY_ID[layerGroupId] = {
        ...layerGroupProperties,
        name,
        layers,
        state: 'init'
    };
}

// set up layers, default values
for (let layerGroupId in LAYER_GROUPS_BY_ID) {
    let layerGroupLayers = CONFIG.map.layers[layerGroupId].layers;
    for (let layerId in layerGroupLayers) {
        let layer = layerGroupLayers[layerId];
        // reassign some values
        let name = layer.name || layerId;
        let id = layerId;
        // assign new layer
        LAYERS_BY_ID[layerId] = {
            ...layer,
            id,
            name,
            layerGroupId
        };
        // add layer ID to layer groups
        LAYER_GROUPS_BY_ID[layerGroupId].layers.push(layerId);
    }
}

export function getPhotoURLs (layerId, photoProperties) {
    let urls = {};
    switch (layerId) {
        case "photos_1976":
        case "photos_2007": {
            let base = CONFIG.resources.photos.urlBase;
            let lakeName = photoProperties["Great Lake"].replace(/ /gi, "");
            let year = photoProperties["Year"];
            let fileName = photoProperties["File Name"];
            for (let size in CONFIG.resources.photos.sizes) {
                let sizeDir = CONFIG.resources.photos.sizes[size];
                let modifiedFilename = fileName;
                if(size !== "original") {
                    let parts = fileName.split(".");
                    parts[0] += "_" + sizeDir;
                    modifiedFilename = parts.join(".");
                }
                urls[size] = [base,lakeName,year,sizeDir,modifiedFilename].join("/");
            }
            break;
        }
        case "photos_2016": {
            let base = CONFIG.resources.photos_2016.urlBase;
            let fileName = photoProperties.filename;
            urls.original = [base,fileName].join("/");
            urls.popup = [base,"popup",fileName].join("/");
            break;
        }
        case "photos_2017": {
            let base = CONFIG.resources.photos_2017.urlBase;
            let fileName = photoProperties.id;
            let ext = CONFIG.resources.photos_2017.extension;
            urls.original = [base,fileName].join("/") + ext;
            urls.popup = [base,"popup",fileName].join("/") + ext;
            break;
        }
        case "photos_2012": {
            let base = CONFIG.resources.photos_2012.urlBase;
            let fileName = photoProperties.imageId;
            let ext = CONFIG.resources.photos_2012.extension;
            urls.original = [base,fileName].join("/") + ext;
            urls.popup = [base,"popup",fileName].join("/") + ext;
            break;
        }
        default:
        break;
    }
    return urls;
}

export function getProfileURLs (featureProperties) {
    let bluffXls = CONFIG.resources.profiles.pathToXls.bluff;
    let bathyXls = CONFIG.resources.profiles.pathToXls.bathy;
    let bluffJson = CONFIG.resources.profiles.pathToJson.bluff;
    let bathyJson = CONFIG.resources.profiles.pathToJson.bathy;
    let urls = {
        bluffXls: false,
        bathyXls: false,
        bathyJson: false,
        bluffJson: false
    };
    if(featureProperties.bluff_xls) {
        urls.bluffXls = bluffXls + featureProperties.bluff_xls;
        urls.bluffJson = bluffJson+ featureProperties.bluff_json;
    }
    if(featureProperties.bathy_xls) {
        urls.bathyXls = bathyXls +  featureProperties.bathy_xls;
        urls.bathyJson = bathyJson + featureProperties.bathy_json;
    }
    return urls;
}

export { LAYERS_BY_ID, LAYER_GROUPS_BY_ID, BASEMAPS_BY_ID };
